FAL_API_KEY="your_fal_api_key"
NEXT_PUBLIC_FAL_API_KEY="your_fal_api_key" # Si tu veux appeler depuis le frontend Ã‰tape 2 : CrÃ©er le client API SEEDREAMCrÃ©e lib/seedream.ts :TypeScript   import { fal } from "@fal-ai/client";

interface SeedreamParams {
  prompt: string;
  size?: string;
  seed?: number;
  guidanceScale?: number;
  numImages?: number;
  responseFormat?: "url" | "b64_json";
  watermark?: boolean;
}

export class SeedreamClient {
  private apiKey: string;

  constructor(apiKey: string) {
    this.apiKey = apiKey;
    fal.config({ credentials: apiKey });
  }

  async generateImage(params: SeedreamParams): Promise<string> {
    try {
      const result: any = await fal.subscribe(
        "fal-ai/bytedance/seedream/v4/text-to-image",
        {
          input: {
            prompt: params.prompt,
            size: params.size || "1024x1024",
            seed: params.seed || -1,
            guidance_scale: params.guidanceScale || 2.5,
            num_images: params.numImages || 1,
            response_format: params.responseFormat || "url",
            watermark: params.watermark || false,
          },
          logs: true,
          onQueueUpdate: (update) => {
            if (update.status === "IN_PROGRESS") {
              update.logs?.forEach((log: any) => console.log(log.message));
            }
          },
        }
      );

      // Fal.ai retourne un tableau d'images
      return result.data.images[0].url;
    } catch (error) {
      console.error("Seedream generation failed:", error);
      throw new Error(`Seedream API error: ${error.message}`);
    }
  }
} Ã‰tape 3 : CrÃ©er le composant de nÅ“udCrÃ©e components/nodes/SeedreamGenerationNode.tsx :TypeScript   import { memo } from 'react';
import { Handle, Position, NodeProps } from '@xyflow/react';
