function renderFrames(){
  const grid = qs("#framesGrid");
  grid.innerHTML = "";
  const step = currentStep();
  if (!step) return;

  step.screens.forEach((screen, idx) => {
    const card = document.createElement("div");
    card.className = "frameCard";

    const src = screen.src || screen.url; // compat

    card.innerHTML = `
      <div class="cardTop">
        <div class="url" title="${src}">${src}</div>
        <div class="muted">Ã‰cran ${idx+1}</div>
      </div>

      <div style="position:absolute; inset:36px 0 0 0; display:grid; place-items:center; background:#0c1426;">
        <img
          src="${src}"
          alt=""
          style="max-width:100%; max-height:100%; object-fit:contain; display:block;"
          draggable="false"
        />
      </div>

      <div class="overlay" data-screen-url="${encodeURIComponent(src)}"></div>
    `;

    const overlay = card.querySelector(".overlay");
    overlay.classList.toggle("annotate", state.annotateMode);

    overlay.addEventListener("click", (e) => {
      if (!state.annotateMode) return;
      const rect = overlay.getBoundingClientRect();
      const x = (e.clientX - rect.left) / rect.width;  // %
      const y = (e.clientY - rect.top) / rect.height;  // %
      addPin(src, x, y);
    });

    grid.appendChild(card);
  });

  renderPins();
}

