      updateNodeData(id, { status: 'error', error: error.message });
      exec.fail(id, error);
    }
  };

  return (
    <NodeWrapper {...props} onExec={handleGenerate}>
      <div className="space-y-4">
        <SettingsSection title="Prompt">
          <textarea
            value={data.prompt || ''}
            onChange={(e) => updateNodeData(id, { prompt: e.target.value })}
            className="w-full px-3 py-2 bg-gray-900 border border-gray-700 rounded-md text-sm"
            rows={3}
            placeholder="Enter your prompt (max 500 chars)..."
            maxLength={500}
          />
        </SettingsSection>

        <SettingsSection title="Parameters">
          <div className="grid grid-cols-2 gap-2">
            <select
              value={data.size || '1024x1024'}
              onChange={(e) => updateNodeData(id, { size: e.target.value })}
              className="px-3 py-2 bg-gray-900 border border-gray-700 rounded-md text-sm"
            >
              <option value="1024x1024">1024x1024 (1:1)</option>
              <option value="864x1152">864x1152 (3:4)</option>
              <option value="1152x864">1152x864 (4:3)</option>
              <option value="1280x720">1280x720 (16:9)</option>
              <option value="720x1280">720x1280 (9:16)</option>
              <option value="2048x2048">2048x2048 (2K)</option>
            </select>

            <input
              type="number"
              value={data.seed || -1}
              onChange={(e) => updateNodeData(id, { seed: parseInt(e.target.value) })}
              className="px-3 py-2 bg-gray-900 border border-gray-700 rounded-md text-sm"
