            />
            <span className="text-xs text-gray-500">Higher = more prompt adherence</span>
          </div>
        </SettingsSection>

        <Handle type="target" position={Position.Left} id="prompt" className="handle-text" />
        <Handle type="source" position={Position.Right} id="image" className="handle-image" />
      </div>
    </NodeWrapper>
  );
});

