<script src="https://cdn.jsdelivr.net/npm/js-yaml@4/dist/js-yaml.min.js"></script>
<script>
  // ---------- Helpers ----------
  const qs = (s, el=document) => el.querySelector(s);
  const qsa = (s, el=document) => [...el.querySelectorAll(s)];
  const uid = () => Math.random().toString(16).slice(2) + Date.now().toString(16);

  const PIN_COLORS = ["var(--pin1)","var(--pin2)","var(--pin3)","var(--pin4)","var(--pin5)"];

  // Storage key (par projet)
  const STORAGE_KEY = "scenario_player_annotations_v1";

  // State
  let data = null;
  let state = {
    roleId: null,
    scenarioId: null,
    stepIdx: 0,
    annotateMode: false
  };

  // annotations: { [scenarioId]: { [stepId]: { notes:[], pinsByScreen:{[screenUrl]: pins[] } } } }
  let store = loadStore();

  function loadStore(){
    try { return JSON.parse(localStorage.getItem(STORAGE_KEY) || "{}"); }
    catch { return {}; }
  }
  function saveStore(){
    localStorage.setItem(STORAGE_KEY, JSON.stringify(store));
  }

  function ensureStepBucket(scenarioId, stepId){
    store[scenarioId] ||= {};
    store[scenarioId][stepId] ||= { notes: [], pinsByScreen: {} };
    return store[scenarioId][stepId];
  }

  // ---------- YAML loading ----------
  async function loadYaml(){
    const res = await fetch("./scenarios.yml", { cache: "no-store" });
    const txt = await res.text();
    return jsyaml.load(txt);
  }

  // ---------- UI binding ----------
  function setAnnotateMode(on){
    state.annotateMode = on;
    qs("#toggleAnnotate").textContent = "Mode annotation : " + (on ? "ON" : "OFF");
    qsa(".overlay").forEach(ov => ov.classList.toggle("annotate", on));
  }

  function currentScenario(){
    return data.scenarios.find(s => s.id === state.scenarioId);
  }
  function currentStep(){
    const sc = currentScenario();
    return sc?.steps?.[state.stepIdx] || null;
  }

  function roleLabel(roleId){
    return (data.roles.find(r => r.id === roleId) || {}).label || roleId;
  }

  function renderScenarioList(){
    const list = qs("#scenarioList");
    list.innerHTML = "";
    const scenarios = data.scenarios.filter(s => s.role === state.roleId);

    scenarios.forEach(sc => {
      const div = document.createElement("div");
      div.className = "item" + (sc.id === state.scenarioId ? " active" : "");
      div.innerHTML = `
        <div class="title">${sc.label}</div>
        <div class="sub">${roleLabel(sc.role)} ‚Ä¢ ${sc.steps.length} √©tapes</div>
      `;
      div.onclick = () => {
        state.scenarioId = sc.id;
        state.stepIdx = 0;
        syncSelects();
        renderAll();
      };
      list.appendChild(div);
    });

    // si aucun scenario s√©lectionn√©
    if (!state.scenarioId && scenarios[0]){
      state.scenarioId = scenarios[0].id;
      state.stepIdx = 0;
      syncSelects();
    }
  }

  function syncSelects(){
    qs("#roleSelect").value = state.roleId;
    qs("#scenarioSelect").value = state.scenarioId;
  }

  function renderTop(){
    qs("#appName").textContent = data.app?.name ? `‚Ä¢ ${data.app.name}` : "";
    const step = currentStep();
    qs("#stepIndex").textContent = step ? `${state.stepIdx+1}/${currentScenario().steps.length}` : "‚Äî";
    qs("#stepTitle").textContent = step?.label || "‚Äî";
    qs("#stepLabel").textContent = step ? `(${step.id})` : "";
  }

  function renderFrames(){
    const grid = qs("#framesGrid");
    grid.innerHTML = "";
    const step = currentStep();
    if (!step) return;

    // au minimum 2 colonnes si assez large (CSS). Ici: on cr√©e N cards
    step.screens.forEach((screen, idx) => {
      const card = document.createElement("div");
      card.className = "frameCard";
      const url = screen.url;

      card.innerHTML = `
        <div class="cardTop">
          <div class="url" title="${url}">${url}</div>
          <div class="muted">√âcran ${idx+1}</div>
        </div>
        <iframe src="${url}" loading="lazy"></iframe>
        <div class="overlay" data-screen-url="${encodeURIComponent(url)}"></div>
      `;

      const overlay = card.querySelector(".overlay");
      overlay.classList.toggle("annotate", state.annotateMode);

      overlay.addEventListener("click", (e) => {
        if (!state.annotateMode) return;
        const rect = overlay.getBoundingClientRect();
        const x = (e.clientX - rect.left) / rect.width;   // 0..1
        const y = (e.clientY - rect.top) / rect.height;   // 0..1
        addPin(url, x, y);
      });

      grid.appendChild(card);
    });

    renderPins();
  }

  function renderPins(){
    const step = currentStep();
    const bucket = ensureStepBucket(state.scenarioId, step.id);

    qsa(".overlay").forEach(overlay => {
      const url = decodeURIComponent(overlay.dataset.screenUrl || "");
      overlay.innerHTML = "";

      const pins = bucket.pinsByScreen[url] || [];
      pins.forEach((p, i) => {
        const el = document.createElement("div");
        el.className = "pin";
        el.style.left = (p.x * 100) + "%";
        el.style.top  = (p.y * 100) + "%";
        el.style.background = PIN_COLORS[p.colorIdx % PIN_COLORS.length];
        el.title = p.title || "Clique: ouvrir note";
        el.textContent = p.number ?? (i+1);

        el.onclick = (ev) => {
          ev.stopPropagation();
          // ouvre/scroll la note li√©e si pr√©sente
          if (p.noteId){
            const noteEl = qs(`[data-note-id="${p.noteId}"]`);
            if (noteEl) noteEl.scrollIntoView({ behavior:"smooth", block:"center" });
          }
        };

        overlay.appendChild(el);
      });
    });
  }

  function renderNotes(){
    const step = currentStep();
    const notesList = qs("#notesList");
    notesList.innerHTML = "";
    if (!step) return;

    const bucket = ensureStepBucket(state.scenarioId, step.id);
    const notes = bucket.notes;

    if (!notes.length){
      notesList.innerHTML = `<div class="muted">Aucune remarque pour cette √©tape.</div>`;
      return;
    }

    notes.forEach((n, idx) => {
      const div = document.createElement("div");
      div.className = "note";
      div.dataset.noteId = n.id;

      const color = PIN_COLORS[n.colorIdx % PIN_COLORS.length];

      div.innerHTML = `
        <div class="meta">
          <span class="pill"><span class="sw" style="background:${color}"></span> ${n.kind}</span>
          <span class="muted">‚Ä¢ ${n.screenUrl ? ("√©cran: " + n.screenUrl) : "g√©n√©ral"}</span>
        </div>
        <textarea placeholder="√âcris ton commentaire‚Ä¶">${escapeHtml(n.text || "")}</textarea>
        <div class="row">
          <button class="danger">Supprimer</button>
          <span class="muted">#${idx+1}</span>
        </div>
      `;

      const ta = div.querySelector("textarea");
      ta.addEventListener("input", () => {
        n.text = ta.value;
        saveStore();
      });

      div.querySelector("button").onclick = () => {
        // supprimer note + d√©lier pins
        bucket.notes = bucket.notes.filter(x => x.id !== n.id);
        // d√©lier tous pins vers cette note
        Object.keys(bucket.pinsByScreen).forEach(url => {
          bucket.pinsByScreen[url] = (bucket.pinsByScreen[url]||[]).map(p => {
            if (p.noteId === n.id) return { ...p, noteId: null, title: "Note supprim√©e" };
            return p;
          });
        });
        saveStore();
        renderNotes();
        renderPins();
      };

      notesList.appendChild(div);
    });
  }

  function addGeneralNote(){
    const step = currentStep();
    const bucket = ensureStepBucket(state.scenarioId, step.id);
    bucket.notes.unshift({
      id: uid(),
      kind: "NOTE",
      screenUrl: null,
      text: "",
      colorIdx: 2
    });
    saveStore();
    renderNotes();
  }

  function addPin(screenUrl, x, y){
    const step = currentStep();
    const bucket = ensureStepBucket(state.scenarioId, step.id);
    bucket.pinsByScreen[screenUrl] ||= [];

    // cr√©e une note li√©e au pin (pratique pour ‚Äúdiscussion‚Äù)
    const noteId = uid();
    const number = bucket.pinsByScreen[screenUrl].length + 1;
    const colorIdx = (number - 1) % PIN_COLORS.length;

    bucket.notes.unshift({
      id: noteId,
      kind: "PIN",
      screenUrl,
      text: "",
      colorIdx
    });

    bucket.pinsByScreen[screenUrl].push({
      id: uid(),
      x, y,
      number,
      colorIdx,
      noteId,
      title: `Pin #${number}`
    });

    saveStore();
    renderNotes();
    renderPins();
  }

  function escapeHtml(s){
    return String(s).replace(/[&<>"']/g, c => ({
      "&":"&amp;","<":"&lt;",">":"&gt;",'"':"&quot;","'":"&#039;"
    }[c]));
  }

  // ---------- Export / Import ----------
  function exportStore(){
    const blob = new Blob([JSON.stringify(store, null, 2)], { type:"application/json" });
    const a = document.createElement("a");
    a.href = URL.createObjectURL(blob);
    a.download = "annotations.json";
    a.click();
    URL.revokeObjectURL(a.href);
  }

  function importStore(file){
    const reader = new FileReader();
    reader.onload = () => {
      try {
        const obj = JSON.parse(reader.result);
        store = obj || {};
        saveStore();
        renderAll();
      } catch(e){
        alert("JSON invalide");
      }
    };
    reader.readAsText(file);
  }

  // ---------- Render all ----------
  function renderAll(){
    renderScenarioList();
    renderTop();
    renderFrames();
    renderNotes();
    setAnnotateMode(state.annotateMode);
    // sync active style
    qsa("#scenarioList .item").forEach((el, i) => {
      // already set by re-render
    });
  }

  // ---------- Init ----------
  async function init(){
    data = await loadYaml();

    // init role
    state.roleId = data.roles?.[0]?.id || null;

    // build role select
    const roleSel = qs("#roleSelect");
    roleSel.innerHTML = "";
    data.roles.forEach(r => {
      const opt = document.createElement("option");
      opt.value = r.id;
      opt.textContent = r.label || r.id;
      roleSel.appendChild(opt);
    });
    roleSel.onchange = () => {
      state.roleId = roleSel.value;
      state.scenarioId = null;
      state.stepIdx = 0;
      renderScenarioList();
      // pick first scenario of role
      const first = data.scenarios.find(s => s.role === state.roleId);
      if (first) state.scenarioId = first.id;
      syncScenarioSelect();
      renderAll();
    };

    // scenario select
    const scSel = qs("#scenarioSelect");
    function syncScenarioSelect(){
      scSel.innerHTML = "";
      const scenarios = data.scenarios.filter(s => s.role === state.roleId);
      scenarios.forEach(sc => {
        const opt = document.createElement("option");
        opt.value = sc.id;
        opt.textContent = sc.label;
        scSel.appendChild(opt);
      });
      if (!state.scenarioId && scenarios[0]) state.scenarioId = scenarios[0].id;
      scSel.value = state.scenarioId;
    }
    syncScenarioSelect();
    scSel.onchange = () => {
      state.scenarioId = scSel.value;
      state.stepIdx = 0;
      renderAll();
    };

    // nav step
    qs("#prevBtn").onclick = () => {
      state.stepIdx = Math.max(0, state.stepIdx - 1);
      renderAll();
    };
    qs("#nextBtn").onclick = () => {
      const sc = currentScenario();
      if (!sc) return;
      state.stepIdx = Math.min(sc.steps.length - 1, state.stepIdx + 1);
      renderAll();
    };

    // annotate toggle
    qs("#toggleAnnotate").onclick = () => setAnnotateMode(!state.annotateMode);

    // notes
    qs("#addGeneralNoteBtn").onclick = addGeneralNote;

    // export/import
    qs("#exportBtn").onclick = exportStore;
    qs("#importBtn").onclick = () => qs("#importFile").click();
    qs("#importFile").addEventListener("change", (e) => {
      const f = e.target.files?.[0];
      if (f) importStore(f);
      e.target.value = "";
    });

    // first render
    roleSel.value = state.roleId;
    renderScenarioList();
    const firstScenario = data.scenarios.find(s => s.role === state.roleId);
    state.scenarioId = firstScenario?.id || data.scenarios?.[0]?.id || null;
    syncScenarioSelect();
    renderAll();
  }

  init();
</script>
